import Vote from "./Vote";
import styled from "@emotion/styled";

const Styled = styled.tr`
  .post {
    display: flex;
    flex-direction: column

    .details {
      color: ##828282;
    }
  }
`;
const Card = (props) => (
  <Styled>
    <td>{props.index}.</td>
    <td><Vote /></td>
    <td className="post">
      <span>{props.short_title}</span>
      <div>
        {props.points} points | Sponsor Name: {props.sponsor_name}{" "}
        {props.latest_major_action_date} | <span>hide</span> |{" "}
        {props.comments.length} comments
      </div>
    </td>
  </Styled>
);

export default Card;
