// import fetch from 'isomorphic-unfetch';
import fetch from 'node-fetch'
import { INTRODUCED_BILLS_URL } from '../utils/constants';
import Card from "../components/Card";
import Layout from "../components/Layout";

function Index(props) {
  return (
    <Layout>
      <table>
        <thead>Chamber: {props.recentBills[0].chamber}</thead>
        <tbody>
          {props.recentBills[0].bills.map((card, index) => (
            <Card key={card.bill_id} {...card} index={index + 100} comments={[]} points={0} />
          ))}
        </tbody>
      </table>
    </Layout>
  );
}

export async function getServerSideProps() {
  const res = await fetch(INTRODUCED_BILLS_URL, {
    method: 'get',
    mode: 'cors',
    headers: {
      'X-API-Key': process.env.PROPUBLICA_API_KEY,
    },
  });
  const data = await res.json();
  return {
    props: { recentBills: data.results }
  };
}

export default Index;
