// }

function Comment(props) {
  const [comment, setComment] = React.useState('');
  // const { data, error } = useSWR('/api/comments', fetcher);
  // console.log(data)
  // console.error(error)
  return (
    <>
      <span>Comments</span>
      <input onChange={setComment} value={comment} />
    </>
  );
}

export async function getServerSideProps({ reg, params }) {
  try {
    console.log('HELLO WORLD');
    const protocol = req
      ? `${req.headers['x-forwarded-proto']}:`
      : location.protocol
    const host = req ? req.headers['x-forwarded-host'] : location.host
    const commentsURL = `${protocol}//${host}/api/comments?post_id=${params.postId}`
    const res = await fetch(commentsURL);
    const data = await res.json()
    console.log('HELLO WORLD');
    return {
      props: { data }
    };
  } catch (e) {
    console.error(e);
  }
}

export default Comment;
