import Vote from "./Vote";
import styled from "@emotion/styled";

const Styled = styled.tr`
  .post {
    display: flex;
    flex-direction: column;

    .details {
      color: ##828282;
      font-size: 10pt;
    }

    .title {
      color: ${props => props.visited ? '#828282' : '#000'};
      fontx-size: 13pt;
    }
  }
`;

const Card = (props) => {
  const [hide, setHide] = React.useState('false');
  return (
    <Styled>
      <td>{props.index}.</td>
      <td><Vote /></td>
      <td className="post">
        <span className="title">{props.short_title}</span>
        <div className="details">
          {props.points} points | Sponsor Name: {props.sponsor_name}{" "}
          {props.latest_major_action_date} | <span>hide</span> |{" "}
          {props.comments.length} comments
      </div>
      </td>
    </Styled>
  );
}

export default Card;
