import fetch from "isomorphic-unfetch";
import { get_SPECIFIC_BILL } from "../../../utils/constants";
import Layout from "../../../components/Layout";
import Vote from "../../../components/Vote";

function Comments({ bill }) {
  return (
    <Layout>
      <span>{bill.short_title}</span>
      <span>{bill.title}</span>
      <span>{bill.bill}</span>
      <span>{bill.sponsor_title}</span>
      <span>{bill.sponsor}</span>
      <span>{bill.sponsor_party}</span>
      <span>{bill.sponsor_state}</span>
      <span>{bill.active}</span>
      <span>{bill.primary_subject}</span>
      <span>{bill.committee_codes}</span>
      <span>{bill.latest_major_action_date}</span>
      <span>{bill.latest_major_action}</span>
      <span>{bill.summary}</span>
      <span>{bill.summary_short}</span>
      <span>votes: {bill.votes.length}</span>
      <Vote points={Math.floor(Math.random() * 100)} />
    </Layout>
  );
}

export async function getServerSideProps({ params }) {
  try {
    const res = await fetch(get_SPECIFIC_BILL(params.congress, params.bill), {
      method: "get",
      mode: "cors",
      headers: {
        "X-API-Key": process.env.PROPUBLICA_API_KEY
      }
    });
    const data = await res.json();
    return {
      props: { bill: data.results[0] }
    };
  } catch (e) {
    console.error(e);
  }
}

export default Comments;
