// import fetch from 'isomorphic-unfetch';
import fetch from 'node-fetch'
import { INTRODUCED_BILLS_URL } from '../utils/constants';
import Card from "../components/Card";

const tempData = [
  {
    title:
      "To amend the Public Health Service Act to authorize the expansion of activities related to Alzheimer's disease, cognitive decline, and brain health under the Alzheimer's Disease and Healthy Aging Program, and for other purposes.",
    points: 0,
    sponsor_name: "Brett Guthrie",
    latest_major_action_date: "2017-11-06",
    comments: [],
  },
];

function Index(props) {
  return (
    <>
      <span>Chamber: {props.recentBills[0].chamber}</span>
      {props.recentBills[0].bills.map((card, index) => (
        <Card key={card.bill_id} {...card} index={index} comments={[]} points={0} />
      ))}
    </>
  );
}

export async function getServerSideProps() {
  const res = await fetch(INTRODUCED_BILLS_URL, {
    method: 'get',
    mode: 'cors',
    headers: {
      'X-API-Key': process.env.PROPUBLICA_API_KEY,
    },
  });
  const data = await res.json();
  return {
    props: { recentBills: data.results }
  };
}

export default Index;
